package ar.edu.unju.fi.ejercicio10;

public class Ejercicio10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int NUMERO_DE_RESTA = 20;
		int numero = 320;
		while (numero >= 160) {
			System.out.print(numero + " ");
			numero = numero - NUMERO_DE_RESTA;
		}
	}

}
